--Screen Notes: 
-- 1vw = 1.675vh
-- Item Cards
-- 5.5vw - 6x14.6vw - 6.9vw
-- 10vh - 2x31.825vh - 26.35vh

CurrentX = 100*Screen.getMouseX();
CurrentY = 100*Screen.getMouseY();

-- Select Industry Pressed
if (CurrentX > 5 and CurrentX < 95 and CurrentY > 10 and CurrentY < 70 and not Running) then

    XItemIndex = math.ceil((CurrentX - 5)/14.6); 
    YItemIndex = math.ceil((CurrentY - 10)/31.825);

    XItemIndex = math.max(math.min(XItemIndex, 6), 1);
    YItemIndex = math.max(math.min(YItemIndex, 2), 1);

    CompositeItemIndex = RowIndex*6 + (YItemIndex - 1)*6 + XItemIndex;

end

-- Run Button Pressed
if (CurrentX > 78 and CurrentX < 84 and CurrentY > 79.95 and CurrentY < 90 and not Running) then

    if CurrentSchematic > 0 then
        RowIndex = math.floor(CompositeItemIndex/6);
        RowIndex = math.max(math.min(RowIndex, math.floor(NumItems/6) - 1), 0);
        Industry.setCurrentSchematic(CurrentSchematic);
        Industry.batchStart(ProduceCount);
        Running = true;
    end

end

-- Stop Button Pressed
if (CurrentX > 85.4 and CurrentX < 91 and CurrentY > 80.22 and CurrentY < 89.6) then

    Running = false;
    Industry.hardStop();

end

-- Initialization

unit.setTimer("Update");

RowIndex = 0;
NumItems = 0;
Progress = 100;
ProduceCount = 1;
Running = false;
StatusMsg = "Production Ready";
PlayColor = "#a46666"
EfficiencySkillLevel = 0 --export: Relevant Efficiency Skill Level
EfficiencySkillStepSize = 0.05 --export: Relevant Efficiency Skill Step Size

for _, __ in pairs(ItemsList) do
    NumItems = NumItems + 1;
end

HaveDatabank = false;

for key, value in pairs(unit) do
    if type(value) == "table" and type(value.export) == "table" then
        if value.getNbKeys then
            HaveDatabank = true;
        end
    end
end

if not(Industry.getStatus() == "STOPPED") and HaveDatabank then

    Running = (Databank.getIntValue("Running") == 1);
    ProduceCount = Databank.getIntValue("Count");
    CompositeItemIndex = Databank.getIntValue("CompositeItemIndex");
    RowIndex = Databank.getIntValue("RowIndex");

elseif (not(Industry.getStatus() == "STOPPED") and not(HaveDatabank)) then
