    RowIndex = RowIndex - math.floor(math.max(math.min(system.getMouseWheel(), 1), -1)+ 0.5);

    RowIndex = math.max(math.min(RowIndex, math.floor(NumItems/6) - 1), 0);

end

-- Counter Scroll
if (CurrentX > 62 and CurrentX < 76 and CurrentY > 80.625 and CurrentY < 89 and not Running) then

    ProduceCount = ProduceCount + math.floor(math.max(math.min(system.getMouseWheel(), 1), -1)+ 0.5);

    ProduceCount = math.max(math.min(ProduceCount,999), 1);

end

-- Counter and Loading Bar Progress
if Running then

    ProduceCount = ProduceCount - Industry.getCycleCountSinceStartup();
    StatusMsg = Industry.getStatus():gsub("_"," ");
    Databank.setIntValue("Running",1);
    Databank.setIntValue("Count",ProduceCount);
    Databank.setIntValue("CompositeItemIndex",CompositeItemIndex);
    Databank.setIntValue("RowIndex",RowIndex);
    PlayColor = "#bbb";

elseif not Running then

    Databank.setIntValue("Running",0);
    Databank.setIntValue("Count",0);
    StatusMsg = "Production Ready";
    PlayColor = "#A46666";

end

